import React from "react";
import {
  ConstructorElement,
  DragIcon,
  Button,
} from "@ya.praktikum/react-developer-burger-ui-components";
import { data } from "../../utils/data";
import currency from "../../images/currency-icon.svg";
import BCStyle from "./BurgerConstructor.module.css";
import PropTypes from "prop-types";
import { ingredientPropType } from "../../utils/prop-types";

class BurgerConstructor extends React.Component {
  render() {
    return (
      <section className={BCStyle.section + " mt-25"}>
        <div className="mb-4 ml-8">
          <ConstructorElement
            type="top"
            isLocked={true}
            text={data[0].name + " (верх)"}
            price={20}
            thumbnail={data[0].image}
          />
        </div>
        <ul className={BCStyle.list}></ul> {/* Closing tag added here */}
        <div className="mt-4 ml-8">
          <ConstructorElement
            type="bottom"
            isLocked={true}
            text={data[0].name + " (низ)"}
            price={20}
            thumbnail={data[0].image}
          />
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            alignItems: "center",
          }}
          className="mt-10"
        >
          <p className="text text_type_digits-medium mr-2">610</p>
          <img alt="валюта" src={currency} />
          <Button
            htmlType="submit"
            type="primary"
            size="large"
            style={{ marginLeft: "40px" }}
          >
            Оформить заказ
          </Button>
        </div>
      </section>
    );
  }
}


BurgerConstructor.propTypes = {
  data: PropTypes.arrayOf(ingredientPropType),
};

export default BurgerConstructor;
