import React, { useState } from 'react';
import { data } from '../../utils/data';
import {
  ConstructorElement,
  Button,
  DragIcon,
} from '@ya.praktikum/react-developer-burger-ui-components';
import currency from '../../images/currency-icon.svg';
import BCStyle from './BurgerConstructor.module.css';
import Card from '../Card/Card';

const BurgerConstructor = ({
  constructorItems,
  onDeleteFromConstructor,
  totalPrice,
  selectedItems = [7, 2, 8, 11, 11, 1, 3, 4, 10, 12, 13],
}) => {
  const handleDeleteFromConstructor = (itemId, price, count) => {
    const updatedItems = selectedItems.filter((item) => item !== itemId);
    onDeleteFromConstructor(itemId, price, count, updatedItems);
  };

  return (
    <section className={BCStyle.section + ' mt-25 pl-4'}>
      <div style={{ display: 'flex', flexDirection: 'column'}}>
        <div className={BCStyle.item + ' mb-4 ml-8'}>
          <ConstructorElement
            type="top"
            isLocked={true}
            text={data[0].name + ' (верх)'}
            price={data[0].price}
            thumbnail={data[0].image}
          />
        </div>
        <ul className={BCStyle.list}>
          {selectedItems.map((itemId, index) => (
            <li className={BCStyle.item} key={`selected-${itemId}-${index}`}>
              <DragIcon />
              <ConstructorElement
                text={data[itemId].name}
                price={data[itemId].price}
                thumbnail={data[itemId].image}
                handleClose={() =>
                  handleDeleteFromConstructor(
                    itemId,
                    data[itemId].price,
                    data[itemId].count
                  )
                }
              />
            </li>
          ))}
          {constructorItems.map((item) => (
            <li className={BCStyle.item} key={`constructor-${item.id}`}>
              <DragIcon />
              <ConstructorElement
                type={item.type}
                isLocked={item.isLocked}
                text={item.name}
                price={item.price}
                thumbnail={item.image}
                handleClose={() =>
                  handleDeleteFromConstructor(item.id, item.price, item.count)
                }
              />
            </li>
          ))}
        </ul>

        <div className={BCStyle.item + ' mt-4 ml-8'}>
          <ConstructorElement
            type="bottom"
            isLocked={true}
            text={data[0].name + ' (низ)'}
            price={data[0].price}
            thumbnail={data[0].image}
          />
        </div>
      </div>
      <div
        style={{
          display: 'flex',
          justifyContent: 'flex-end',
          alignItems: 'center',
        }}
        className="mt-10"
      >
        <p className="text text_type_digits-medium mr-2">{610 + totalPrice}</p>
        <img alt="валюта" src={currency} />
        <Button
          htmlType="submit"
          type="primary"
          size="large"
          style={{ marginLeft: '40px' }}
        >
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
