import React from "react";

const Card = ({ image, price, name, onAdd }) => {
  return (
    <li>
      <img src={image} alt={name} />
      <p>{price}</p>
      <h3>{name}</h3>
      <button onClick={onAdd}>Add</button> {/* Button to add the item */}
    </li>
  );
};

export default Card;
