import React from 'react';
import { ConstructorElement, Button, DragIcon } from '@ya.praktikum/react-developer-burger-ui-components';
import currency from '../../images/currency-icon.svg';
import BCStyle from './BurgerConstructor.module.css';
import Card from '../Card/Card';

const BurgerConstructor = ({ constructorItems, onDeleteFromConstructor, totalPrice }) => {
  const handleDeleteFromConstructor = (itemId, price, count) => {
    const updatedItems = constructorItems.filter(item => item !== itemId);
    onDeleteFromConstructor(itemId, price, count, updatedItems);
  };

  return (
    <section className={BCStyle.section + ' mt-25'}>
      <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <div className={BCStyle.item + ' mb-4 ml-8'}>
          <ConstructorElement
            type="top"
            isLocked={true}
            text="Верх булки"
            price={0}
            thumbnail=""
          />
        </div>
        <ul className={BCStyle.list}>
          {constructorItems.map(item => (
            <li className={BCStyle.item} key={`constructor-${item.id}`}>
              <DragIcon />
              <ConstructorElement
                type={item.type}
                isLocked={item.isLocked}
                text={item.name}
                price={item.price}
                thumbnail={item.image}
                handleClose={() =>
                  handleDeleteFromConstructor(item.id, item.price, item.count)
                }
              />
            </li>
          ))}
        </ul>
        <div className={BCStyle.item + ' mt-4 ml-8'}>
          <ConstructorElement
            type="bottom"
            isLocked={true}
            text="Низ булки"
            price={0}
            thumbnail=""
          />
        </div>
      </div>
      <div className={`${BCStyle['item-total']} mt-10`}>
        <p className={`${BCStyle['item-total-price']} text text_type_digits-medium mr-2`}>{totalPrice}</p>
        <img alt="валюта" src={currency} />
        <Button type="primary" size="large" style={{ marginLeft: '40px' }}>
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
