import React, { useState } from 'react';
import AppHeader from '../AppHeader/AppHeader';
import BurgerConstructor from '../BurgerConstructor/BurgerConstructor';
import BurgerIngredients from '../BurgerIngredients/BurgerIngredients';
import AppStyle from './App.module.css';

const App = () => {
  const [constructorItems, setConstructorItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  const addToConstructor = (product) => {
    const existingProduct = constructorItems.find(item => item.id === product.id);

    if (existingProduct) {
      const updatedItems = constructorItems.map(item => {
        if (item.id === product.id) {
          return { ...item, count: item.count + 1 };
        }
        return item;
      });
      setConstructorItems(updatedItems);
    } else {
      setConstructorItems(prevItems => [...prevItems, { ...product, id: Date.now(), count: 1 }]);
    }

    setTotalPrice(prevPrice => prevPrice + product.price);
  };

  const deleteFromConstructor = (productId, price) => {
    setConstructorItems(prevItems =>
      prevItems.map(item => {
        if (item.id === productId) {
          if (item.count > 1) {
            return { ...item, count: item.count - 1 };
          }
          return null;
        }
        return item;
      }).filter(Boolean)
    );

    setTotalPrice(prevPrice => prevPrice - price);
  };

  return (
    <>
      <AppHeader />
      <main className={AppStyle.content}>
        <BurgerIngredients onAddToConstructor={addToConstructor} />
        <BurgerConstructor
          constructorItems={constructorItems}
          onDeleteFromConstructor={deleteFromConstructor}
          totalPrice={totalPrice}
        />
      </main>
    </>
  );
};

export default App;
