import React, { useState, useEffect, useRef } from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import Card from "../Card/Card";
import BIStyle from "./BurgerIngredients.module.css";

const BurgerIngredients = ({ data, selectedItems, onTabClick, handleAddToConstructor }) => {
  const bunRef = useRef(null);

  const [currentTab, setCurrentTab] = useState("bun");
  const [items, setItems] = useState([]);

  useEffect(() => {
    if (data && data.length > 0) {
      setItems(data);
    }
  }, [data]);

  const scrollToBun = () => {
    if (bunRef.current) {
      bunRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  const handleTabClick = (tab) => {
    setCurrentTab(tab);
    scrollToBun();
  };

  return (
    <section className={BIStyle.section}>
      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={currentTab === "bun"}
          onClick={() => handleTabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={currentTab === "sauce"}
          onClick={() => handleTabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={currentTab === "main"}
          onClick={() => handleTabClick("main")}
        >
          Начинки
        </Tab>
      </div>

      <h2
        className="text text_color_inactive text_type_main-medium mt-10 mb-6"
        ref={bunRef}
      >
        Булки
      </h2>
      <ul className={BIStyle.cardsList}>
        {items && items.length > 0 ? (
          items.map((item) => (
            <Card
              key={item._id}
              image={item.image}
              price={item.price}
              count={item.count}
              name={item.name}
              selectedItems={selectedItems}
              onClick={() => handleAddToConstructor(item)}
            />
          ))
        ) : (
          <p>No items available</p>
        )}
      </ul>
    </section>
  );
};

export default BurgerIngredients;
