import React, { useState, useEffect, useRef } from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components/dist/ui/tab";
import { data } from "../../utils/data";
import BIStyle from "./BurgerIngredients.module.css";
import Card from "../Card/Card";
import { useInView } from "react-intersection-observer";

const BurgerIngredients = ({ onAddToConstructor }) => {
  const [currentTab, setCurrentTab] = useState("bun");
  const bunsRef = useRef(null);
  const saucesRef = useRef(null);
  const mainsRef = useRef(null);

  const [inViewBuns, bunsInView] = useInView({ threshold: 0 });
  const [inViewSauces, saucesInView] = useInView({ threshold: 0 });
  const [inViewFilling, fillingInView] = useInView({ threshold: 0 });

  const onTabClick = (e) => {
    if (e === "bun") {
      bunsRef.current?.scrollIntoView({ behavior: "smooth" });
    } else if (e === "sauce") {
      saucesRef.current?.scrollIntoView({ behavior: "smooth" });
    } else if (e === "main") {
      mainsRef.current?.scrollIntoView({ behavior: "smooth" });
    }
  };

  useEffect(() => {
    if (bunsInView) {
      setCurrentTab("bun");
    } else if (saucesInView) {
      setCurrentTab("sauce");
    } else if (fillingInView) {
      setCurrentTab("main");
    }
  }, [bunsInView, fillingInView, saucesInView]);

  const handleAddToConstructor = (product) => {
    const updatedData = data.map((item) => {
      if (item._id === product._id) {
        return { ...item, count: (item.count || 0) + 1 };
      }
      return item;
    });
    onAddToConstructor(updatedData);
  };

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_color_primary text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={currentTab === "bun"}
          onClick={() => onTabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={currentTab === "sauce"}
          onClick={() => onTabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={currentTab === "main"}
          onClick={() => onTabClick("main")}
        >
          Начинки
        </Tab>
      </div>
      <div className={BIStyle.container}>
        <h2 ref={bunsRef} className="text text_color_primary text_type_main-medium mt-10 mb-6">
          Булки
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "bun") {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
        <h2 ref={saucesRef} className="text text_color_primary text_type_main-medium mt-10 mb-6">
          Соусы
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "sauce") {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
        <h2 ref={mainsRef} className="text text_color_primary text_type_main-medium mt-10 mb-6">
          Начинка
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "main") {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
      </div>
    </section>
  );
};

export default BurgerIngredients;
