import React from "react";
import { data } from "../../utils/data";
import {
  ConstructorElement,
  Button,
  DragIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import currency from "../../images/currency-icon.svg";
import BCStyle from "./BurgerConstructor.module.css";
import Card from "../Card/Card";

const BurgerConstructor = ({
  constructorItems,
  onDeleteFromConstructor,
  totalPrice,
}) => {
  const handleDeleteFromConstructor = (productId, price) => {
    onDeleteFromConstructor(productId, price);
  };

  return (
    <section className={BCStyle.section + " mt-25"}>
      <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        <div className={BCStyle.item + "mb-4 ml-8"}>
          <ConstructorElement
            type="top"
            isLocked={true}
            text={data[0].name + " (верх)"}
            price={data[0].price}
            thumbnail={data[0].image}
          />
        </div>
        <ul className={BCStyle.list}>
          {[7, 2, 8, 11, 11, 1, 3, 4, 10, 12, 13].map((index) => (
            <li className={BCStyle.item} key={item.id}>
            <DragIcon />
            <ConstructorElement
              text={item.name}
              price={item.price}
              thumbnail={item.image}
            />
          </li>
        ))}
        {constructorItems.map((item) => (
          <li className={BCStyle.item} key={item.id}>
            <DragIcon />
            <ConstructorElement
              type={item.type}
              isLocked={item.isLocked}
              text={item.name}
              price={item.price}
              thumbnail={item.image}
              handleClose={() =>
                handleDeleteFromConstructor(item.id, item.price, item.count)
              }
            />
          </li>
        ))}
      </ul>
      <div className={BCStyle.item + " mt-4 ml-8"}>
        <ConstructorElement
          type="bottom"
          isLocked={true}
          text={data[0].name + " (низ)"}
          price={data[0].price}
          thumbnail={data[0].image}
        />
      </div>
    </div>
    <div
      style={{
        display: "flex",
        justifyContent: "flex-end",
        alignItems: "center",
      }}
      className="mt-10"
    >
      <p className="text text_type_digits-medium mr-2">
        {610 + totalPrice}
      </p>
      <img alt="валюта" src={currency} />
      <Button
        htmlType="submit"
        type="primary"
        size="large"
        style={{ marginLeft: "40px" }}
      >
        Оформить заказ
      </Button>
    </div>
  </section>
);
};

export default BurgerConstructor;
