import React from "react";
import {
  ConstructorElement,
  Button,
} from "@ya.praktikum/react-developer-burger-ui-components";
import { data } from "../../utils/data";
import currency from "../../images/currency-icon.svg";
import BCStyle from "./BurgerConstructor.module.css";

const BurgerConstructor = () => {
  return (
    <section className={BCStyle.section + " mt-25"}>
      <div className={BCStyle.list}>
        <ConstructorElement
          type="top"
          isLocked={true}
          text={data[0].name + " (верх)"}
          price={20}
          thumbnail={data[0].image}
        />
        <ConstructorElement
          type="bottom"
          text={data[5].name}
          price={data[5].price}
          thumbnail={data[5].image}
        />
        <ConstructorElement
          type="bottom"
          text={data[4].name}
          price={data[4].price}
          thumbnail={data[4].image}
        />
        <ConstructorElement
          type="bottom"
          text={data[7].name}
          price={data[7].price}
          thumbnail={data[7].image}
        />
        <ConstructorElement
          type="bottom"
          text={data[8].name}
          price={data[8].price}
          thumbnail={data[8].image}
        />
        <ConstructorElement
          type="bottom"
          text={data[8].name}
          price={data[8].price}
          thumbnail={data[8].image}
        />
        <ConstructorElement
          type="bottom"
          isLocked={true}
          text={data[0].name + " (вниз)"}
          price={20}
          thumbnail={data[0].image}
        />
        <ConstructorElement
          type="bottom"
          text={data[8].name}
          price={data[8].price}
          thumbnail={data[8].image}
        />
        <ConstructorElement
          type="bottom"
          text={data[8].name}
          price={data[8].price}
          thumbnail={data[8].image}
        />
        <ConstructorElement
          type="bottom"
          text={data[8].name}
          price={data[8].price}
          thumbnail={data[8].image}
        />{" "}
        <ConstructorElement
          type="bottom"
          text={data[8].name}
          price={data[8].price}
          thumbnail={data[8].image}
        />{" "}
        <ConstructorElement
          type="bottom"
          text={data[8].name}
          price={data[8].price}
          thumbnail={data[8].image}
        />
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          alignItems: "center",
        }}
        className="mt-10"
      >
        <p className="text text_type_digits-medium mr-2">610</p>
        <img alt="валюта" src={currency} />
        <Button
          htmlType="submit"
          type="primary"
          size="large"
          style={{ marginLeft: "40px" }}
        >
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
