import React, { useState } from "react";
import {
  ConstructorElement,
  Button,
} from "@ya.praktikum/react-developer-burger-ui-components";
import { data } from "../../utils/data";
import currency from "../../images/currency-icon.svg";
import BCStyle from "./BurgerConstructor.module.css";

const BurgerConstructor = ({ onAddToConstructor, onDeleteFromConstructor }) => {
  const [constructorItems, setConstructorItems] = useState([]);

  const addToConstructor = (product) => {
    setConstructorItems((prevItems) => [...prevItems, product]);
  };

  const deleteFromConstructor = (product) => {
    setConstructorItems((prevItems) =>
      prevItems.filter((item) => item !== product)
    );
  };

  const handleAddToConstructor = (product) => {
    addToConstructor(product);
    onAddToConstructor(product);
  };

  const handleDeleteFromConstructor = (product) => {
    deleteFromConstructor(product);
    onDeleteFromConstructor(product);
  };

  return (
    <section className={BCStyle.section + " mt-25"}>
      <div className={BCStyle.list}>
        <ConstructorElement
          type="top"
          isLocked={true}
          text={data[0].name + " (верх)"}
          price={20}
          thumbnail={data[0].image}
          onClick={() => handleAddToConstructor(data[0])}
          onDelete={() => handleDeleteFromConstructor(data[0])}
        />
        {/* Repeat the above pattern for other ConstructorElement components */}
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          alignItems: "center",
        }}
        className="mt-10"
      >
        <p className="text text_type_digits-medium mr-2">610</p>
        <img alt="валюта" src={currency} />
        <Button
          htmlType="submit"
          type="primary"
          size="large"
          style={{ marginLeft: "40px" }}
        >
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
