import React from 'react';
import PropTypes from 'prop-types';
import IngredientCategory from './IngredientCategory/IngredientCategory';
import AppStyle from './BurgerIngredients.module.css';

const BurgerIngredients = ({ onAddToConstructor, constructorItems }) => {
  return (
    <section className={AppStyle.ingredients}>
      <h2 className={AppStyle.title}>Burger Ingredients</h2>
      <div className={AppStyle.categories}>
        {ingredientCategories.map(category => (
          <IngredientCategory
            key={category.name}
            category={category}
            onAddToConstructor={onAddToConstructor}
            constructorItems={constructorItems} // Pass constructorItems as prop
          />
        ))}
      </div>
    </section>
  );
};

BurgerIngredients.propTypes = {
  onAddToConstructor: PropTypes.func.isRequired,
  constructorItems: PropTypes.array.isRequired,
};

export default BurgerIngredients;
