import React from "react";
import { data } from "../../utils/data";
import {
  ConstructorElement,
  Button,
  DragIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import currencyIcon from "../../images/currency-icon.svg";
import BCStyle from "./BurgerConstructor.module.css";
import Card from "../Card/Card";

const BurgerConstructor = ({
  constructorItems,
  onDeleteFromConstructor,
  totalPrice,
}) => {
  const handleDeleteFromConstructor = (productId, price) => {
    onDeleteFromConstructor(productId, price);
  };

  return (
    <section className={`${BCStyle.section} mt-25`}>
      <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        <div className={`${BCStyle.item} mb-4 ml-8`}>
          <ConstructorElement
            type="top"
            isLocked={true}
            text={`${data[0].name} (верх)`}
            price={data[0].price}
            thumbnail={data[0].image}
          />
        </div>
        <ul className={BCStyle.list}>
          {constructorItems.map((item) => (
            <li className={BCStyle.item} key={item.id}>
              <DragIcon type="primary" />
              <ConstructorElement
                type={item.type}
                isLocked={item.isLocked}
                text={item.name}
                price={item.price}
                thumbnail={item.image}
                handleClose={() =>
                  handleDeleteFromConstructor(item.id, item.price)
                }
              />
            </li>
          ))}
        </ul>
        <div className={`${BCStyle.item} mt-4 ml-8`}>
          <ConstructorElement
            type="bottom"
            isLocked={true}
            text={`${data[0].name} (низ)`}
            price={data[0].price}
            thumbnail={data[0].image}
          />
        </div>
      </div>
      <div className={`${BCStyle.totalPrice} mt-10`}>
        <p className="text text_type_digits-medium mr-2">
          {610 + totalPrice}
        </p>
        <img
          className={BCStyle.currencyIcon}
          src={currencyIcon}
          alt="валюта"
        />
        <Button type="primary" size="large" className={BCStyle.orderButton}>
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
