import React, { useState } from 'react';
import { ConstructorElement, Button } from '@ya.praktikum/react-developer-burger-ui-components';
import { data } from '../../utils/data';
import currency from '../../images/currency-icon.svg';
import BCStyle from './BurgerConstructor.module.css';

const BurgerConstructor = () => {
  const [selectedItems, setSelectedItems] = useState([]);

  const addItemToConstructor = (item) => {
    setSelectedItems([...selectedItems, item]);
  };

  const removeItemFromConstructor = (item) => {
    const updatedItems = selectedItems.filter((selectedItem) => selectedItem._id !== item._id);
    setSelectedItems(updatedItems);
  };

  const getItemCount = (item) => {
    const count = selectedItems.filter((selectedItem) => selectedItem._id === item._id).length;
    return count;
  };

  const handleCardClick = (item) => {
    if (selectedItems.some((selectedItem) => selectedItem._id === item._id)) {
      removeItemFromConstructor(item);
    } else {
      addItemToConstructor(item);
    }
  };

  return (
    <section className={BCStyle.section + " mt-25"}>
      <div className="mb-4 ml-8">
        <ConstructorElement
          type="top"
          isLocked={true}
          text={data[0].name + " (верх)"}
          price={20}
          thumbnail={data[0].image}
        />
      </div>
      <div className={BCStyle.list}>
        {selectedItems.map((item) => (
          <div key={item._id} className={BCStyle.listItem}>
            <p>{item.name}</p>
            <p>{getItemCount(item)}</p>
          </div>
        ))}
      </div>
      <div className="mt-4 ml-8">
        <ConstructorElement
          type="bottom"
          isLocked={true}
          text={data[0].name + " (вниз)"}
          price={20}
          thumbnail={data[0].image}
        />
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          alignItems: "center",
        }}
        className="mt-10"
      >
        <p className="text text_type_digits-medium mr-2">610</p>
        <img alt="валюта" src={currency} />
        <Button
          htmlType="submit"
          type="primary"
          size="large"
          style={{ marginLeft: "40px" }}
        >
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
