// Card.js
import React from 'react';
import { CurrencyIcon, Counter } from '@ya.praktikum/react-developer-burger-ui-components';
import CStyle from './Card.module.css';

const Card = (props) => {
  return (
    <li className={CStyle.card}>
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          position: 'relative',
          justifyContent: 'center',
        }}
        className="pl-4 pr-4"
        onClick={props.onClick} // Add onClick event handler to the wrapping div
      >
        <img src={props.image} alt={props.name} />
        {props.count && <Counter count={props.count} size="default" />}
      </div>
      <div style={{ display: 'flex' }} className="mt-2 mb-2">
        <p className="text text_type_digits-default mr-2">{props.price}</p>
        <CurrencyIcon type="primary" />
      </div>
      <p style={{ minHeight: '48px', textAlign: 'center' }} className="text text_type_main-default">
        {props.name}
      </p>
    </li>
  );
};

export default Card;
