import React, { useState } from 'react';
import { CurrencyIcon, Counter, Button } from "@ya.praktikum/react-developer-burger-ui-components";
import CStyle from './Card.module.css';

const Card = (props) => {
  const [count, setCount] = useState(props.count || 0);

  useEffect(() => {
    if (props.selectedItems.includes(props.id)) {
      const selectedCount = props.selectedItems.filter((itemId) => itemId === props.id).length;
      setCount(selectedCount);
    }
  }, [props.selectedItems, props.id]);

  const handleItemClick = () => {
    setCount(count + 1);
    if (props.onClick) {
      props.onClick({ ...props, count: count + 1 });
    }
  };

  return (
    <li className={CStyle.card}>
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          position: 'relative',
          justifyContent: 'center',
        }}
        className="pl-4 pr-4"
        onClick={handleItemClick}
      >
        <img src={props.image} alt={props.name} />
        {count > 0 && <Counter count={count} size="default" />}
      </div>
      <div style={{ display: 'flex' }} className="mt-2 mb-2">
        <p className="text text_type_digits-default mr-2">{props.price}</p>
        <CurrencyIcon type="primary" />
      </div>
      <p style={{ minHeight: '48px', textAlign: 'center' }} className="text text_type_main-default">
        {props.name}
      </p>
    </li>
  );
};
