import React from "react";
import { data } from "../../utils/data";
import {
  ConstructorElement,
  Button,
  DragIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import currency from "../../images/currency-icon.svg";
import BCStyle from "./BurgerConstructor.module.css";

const BurgerConstructor = ({
  constructorItems,
  onDeleteFromConstructor,
  totalPrice,
}) => {
  const handleDeleteFromConstructor = (productId, price) => {
    onDeleteFromConstructor(productId, price);
  };

  return (
    <section className={`${BCStyle.section} mt-25`}>
      <div className={`${BCStyle.item} mb-4 ml-8`}>
        <ConstructorElement
          type="top"
          isLocked
          text={`${data[0].name} (верх)`}
          price={data[0].price}
          thumbnail={data[0].image}
        />
      </div>
      <ul className={BCStyle.list}>
        {data.slice(7, 14).map((item) => (
          <li className={BCStyle.item} key={item.name}>
            <DragIcon />
            <ConstructorElement
              text={item.name}
              price={item.price}
              thumbnail={item.image}
            />
          </li>
        ))}
        {constructorItems.map((item) => (
          <li className={BCStyle.item} key={item.id}>
            <DragIcon />
            <ConstructorElement
              type={item.type}
              isLocked={item.isLocked}
              text={item.name}
              price={item.price}
              thumbnail={item.image}
              handleClose={() =>
                handleDeleteFromConstructor(item.id, item.price)
              }
            />
          </li>
        ))}
      </ul>
      <div className={`${BCStyle.item} mt-4 ml-8`}>
        <ConstructorElement
          type="bottom"
          isLocked
          text={`${data[0].name} (низ)`}
          price={data[0].price}
          thumbnail={data[0].image}
        />
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          alignItems: "center",
        }}
        className="mt-10"
      >
        <p className="text text_type_digits-medium mr-2">{610 + totalPrice}</p>
        <img alt="валюта" src={currency} />
        <Button
          htmlType="submit"
          type="primary"
          size="large"
          style={{ marginLeft: "40px" }}
        >
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
