import React, { useState, useRef } from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components/dist/ui/tab";
import { data } from "../../utils/data";
import BIStyle from "./BurgerIngredients.module.css";
import Card from "../Card/Card";

const BurgerIngredients = ({ onAddToConstructor }) => {
  const [currentTab, setCurrentTab] = useState("bun");
  const bunRef = useRef(null);
  const sauceRef = useRef(null);
  const mainRef = useRef(null);

  const onTabClick = (e) => {
    if (e === "bun") {
      bunRef.current?.scrollIntoView({ behavior: "smooth" });
    } else if (e === "sauce") {
      sauceRef.current?.scrollIntoView({ behavior: "smooth" });
    } else if (e === "main") {
      mainRef.current?.scrollIntoView({ behavior: "smooth" });
    }
  };

  const handleAddToConstructor = (item) => {
    onAddToConstructor({ ...item, count: item.count + 1 });
  };

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_color_primary text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={currentTab === "bun"}
          onClick={() => onTabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={currentTab === "sauce"}
          onClick={() => onTabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={currentTab === "main"}
          onClick={() => onTabClick("main")}
        >
          Начинки
        </Tab>
      </div>
      <div className={BIStyle.scrollContainer}>
        <div ref={bunRef}>
          <h2 className="text text_type_main-medium mt-10 mb-6">Булки</h2>
          <ul className={BIStyle.list}>
            {data
              .filter((item) => item.type === "bun")
              .map((item) => (
                <Card
                  key={item._id}
                  _id={item._id}
                  image={item.image}
                  name={item.name}
                  price={item.price}
                  onClick={handleAddToConstructor}
                />
              ))}
          </ul>
        </div>
        <div ref={sauceRef}>
          <h2 className="text text_type_main-medium mt-10 mb-6">Соусы</h2>
          <ul className={BIStyle.list}>
            {data
              .filter((item) => item.type === "sauce")
              .map((item) => (
                <Card
                  key={item._id}
                  _id={item._id}
                  image={item.image}
                  name={item.name}
                  price={item.price}
                  onClick={handleAddToConstructor}
                />
              ))}
          </ul>
        </div>
        <div ref={mainRef}>
          <h2 className="text text_type_main-medium mt-10 mb-6">Начинки</h2>
          <ul className={BIStyle.list}>
            {data
              .filter((item) => item.type === "main")
              .map((item) => (
                <Card
                  key={item._id}
                  _id={item._id}
                  image={item.image}
                  name={item.name}
                  price={item.price}
                  onClick={handleAddToConstructor}
                />
              ))}
          </ul>
        </div>
      </div>
    </section>
  );
};

export default BurgerIngredients;
