import React, { useState } from 'react';
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components/dist/ui/tab';
import { data } from '../../utils/data';
import BIStyle from './BurgerIngredients.module.css';
import Card from '../Card/Card';

const BurgerIngredients = ({ onAddToConstructor }) => {
  const [currentTab, setCurrentTab] = useState('bun');
  const selectedItems = [7, 2, 8, 11, 11, 1, 3, 4, 10, 12, 13];

  const selectedCard = data.map((item) => {
    if (selectedItems.includes(item.id)) {
      const count = selectedItems.includes(item.id) ? (item.count || 0) + 1 : (item.count || 0);
      return { ...item, count };
    }
    return item;
  });

  const onTabClick = (tab) => {
    setCurrentTab(tab);
    const ref = document.getElementById(tab);
    if (ref) {
      ref.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleItemClick = (item) => {
    onAddToConstructor({ ...item, count: (item.count || 0) + 1 });
  };

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <div style={{ display: 'flex' }}>
        <Tab value="bun" active={currentTab === 'bun'} onClick={() => onTabClick('bun')}>
          Булки
        </Tab>
        <Tab value="sauce" active={currentTab === 'sauce'} onClick={() => onTabClick('sauce')}>
          Соусы
        </Tab>
        <Tab value="main" active={currentTab === 'main'} onClick={() => onTabClick('main')}>
          Начинки
        </Tab>
      </div>
      <div className={BIStyle.container + ' pl-4 pr-4'}>
        {['bun', 'sauce', 'main'].map((tab) => (
          <div key={tab} id={tab}>
            <h2 className="text text_type_main-medium mt-10 mb-6">
              {tab === 'bun' ? 'Булки' : tab === 'sauce' ? 'Соусы' : 'Начинка'}
            </h2>
            <ul className={`${BIStyle['card-list']} pl-4 pr-4`}>
              {data.map((item) => {
                if (item.type === tab) {
                  return (
                    <Card
                      key={item._id}
                      image={item.image}
                      price={item.price}
                      count={item.count}
                      name={item.name}
                      onClick={() => handleItemClick(item)}
                    />
                  );
                }
                return null;
              })}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
};

export default BurgerIngredients;
