import React from "react";
import {
  ConstructorElement,
  Button,
  CurrencyIcon,
  Counter,
} from "@ya.praktikum/react-developer-burger-ui-components";
import BCStyle from "./BurgerConstructor.module.css";

const BurgerConstructor = ({ constructorItems, onDeleteFromConstructor }) => {
  const handleDeleteFromConstructor = (productId) => {
    onDeleteFromConstructor(productId);
  };

  return (
    <section className={BCStyle.section + " mt-25"}>
      <div className={BCStyle.list}>
        {constructorItems.map((item) => (
          <ConstructorElement
            key={item.id}
            type={item.type}
            isLocked={item.isLocked}
            text={item.name}
            price={item.price}
            thumbnail={item.image}
            handleClose={() => handleDeleteFromConstructor(item.id)}
            quantity={item.count} // Pass the count as the quantity prop
          />
        ))}
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          alignItems: "center",
        }}
        className="mt-10"
      >
        <p className="text text_type_digits-medium mr-2">610</p>
        <CurrencyIcon type="primary" />
        <Button
          htmlType="submit"
          type="primary"
          size="large"
          style={{ marginLeft: "40px" }}
        >
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
