import React, { useState, useRef } from 'react';
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components';
import { data } from '../../utils/data';
import BIStyle from './BurgerIngredients.module.css';
import Card from '../Card/Card';

const Tabs = () => {
  const [current, setCurrent] = useState('one');
  const menuRef = useRef(null);

  const scrollToCategory = (category) => {
    const categoryRef = category === 'bun' ? bunRef.current :
                        category === 'sauce' ? sauceRef.current :
                        mainRef.current;

    if (categoryRef) {
      categoryRef.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  return (
    <div style={{ display: 'flex' }}>
      <Tab value="one" active={current === 'one'} onClick={() => { setCurrent('one'); scrollToCategory('bun'); }}>
        Булки
      </Tab>
      <Tab value="two" active={current === 'two'} onClick={() => { setCurrent('two'); scrollToCategory('sauce'); }}>
        Соусы
      </Tab>
      <Tab value="three" active={current === 'three'} onClick={() => { setCurrent('three'); scrollToCategory('main'); }}>
        Начинки
      </Tab>
    </div>
  );
};

const MenuCategory = ({ category, addItem, selectedItems }) => {
  const categoryData = data.filter((item) => item.type === category);
  const categoryRef = useRef(null);

  const handleAddItem = (item) => {
    addItem(item);
  };

  return (
    <React.Fragment>
      <h2 ref={categoryRef} className="text text_color_primary text_type_main-medium mt-10 mb-6">
        {category === 'bun' ? 'Булки' : category === 'sauce' ? 'Соусы' : 'Начинки'}
      </h2>
      <ul className={BIStyle['card-container'] + ' pl-4 pr-4'}>
        {categoryData.map((item) => (
          <Card
            key={item._id}
            image={item.image}
            price={item.price}
            name={item.name}
            onClick={() => handleAddItem(item)}
            count={selectedItems.filter((selectedItem) => selectedItem._id === item._id).length}
          />
        ))}
      </ul>
    </React.Fragment>
  );
};

const BurgerIngredients = () => {
  const [selectedItems, setSelectedItems] = useState([]);
  const bunRef = useRef(null);
  const sauceRef = useRef(null);
  const mainRef = useRef(null);

  const addItem = (item) => {
    setSelectedItems([...selectedItems, item]);
  };

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_color_primary text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <Tabs />
      <div className={BIStyle['menu-container']}>
        <MenuCategory category="bun" addItem={addItem} selectedItems={selectedItems} ref={bunRef} />
        <MenuCategory category="sauce" addItem={addItem} selectedItems={selectedItems} ref={sauceRef} />
        <MenuCategory category="main" addItem={addItem} selectedItems={selectedItems} ref={mainRef} />
      </div>
    </section>
  );
};

export default BurgerIngredients;
