import React, { useState, useEffect, useRef } from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { data } from "../../utils/data";
import BIStyle from "./BurgerIngredients.module.css";
import Card from "../Card/Card";

const BurgerIngredients = ({ onAddToConstructor }) => {
  const [currentTab, setCurrentTab] = useState("bun");
  const buns = useRef(null);
  const sauces = useRef(null);
  const mains = useRef(null);

  const onTabClick = (e) => {
    if (e === "bun") {
      buns.current?.scrollIntoView({ behavior: "smooth" });
    } else if (e === "sauce") {
      sauces.current?.scrollIntoView({ behavior: "smooth" });
    } else if (e === "main") {
      mains.current?.scrollIntoView({ behavior: "smooth" });
    }
  };

  useEffect(() => {
    if (inViewBuns) {
      setCurrentTab("bun");
    } else if (inViewSauces) {
      setCurrentTab("sauce");
    } else if (inViewFilling) {
      setCurrentTab("main");
    }
  }, [inViewBuns, inViewFilling, inViewSauces]);

  const handleAddToConstructor = (product) => {
    const updatedData = data.map((item) => {
      if (item._id === product._id) {
        return { ...item, count: (item.count || 0) + 1 };
      }
      return item;
    });
    onAddToConstructor(product);
  };

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_color_primary text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <div className={BIStyle.flex}>
        <Tab value="bun" active={currentTab === "bun"} onClick={() => onTabClick("bun")}>
          Булки
        </Tab>
        <Tab value="sauce" active={currentTab === "sauce"} onClick={() => onTabClick("sauce")}>
          Соусы
        </Tab>
        <Tab value="main" active={currentTab === "main"} onClick={() => onTabClick("main")}>
          Начинки
        </Tab>
      </div>
      <div className={BIStyle.container}>
        <h2 ref={buns} className="text text_color_primary text_type_main-medium mt-10 mb-6">
          Булки
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "bun") {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
        <h2 ref={sauces} className="text text_color_primary text_type_main-medium mt-10 mb-6">
          Соусы
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "sauce") {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
        <h2 ref={mains} className="text text_color_primary text_type_main-medium mt-10 mb-6">
          Начинка
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "main") {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
      </div>
    </section>
  );
};

export default BurgerIngredients;
