import React, { useState } from "react";
import BurgerIngredients from "./BurgerIngredients/BurgerIngredients";
import BurgerConstructor from "./BurgerConstructor/BurgerConstructor";

const App = () => {
  const [constructorItems, setConstructorItems] = useState([]);

  const addToConstructor = (product) => {
    setConstructorItems((prevItems) => [...prevItems, product]);
  };

  const deleteFromConstructor = (product) => {
    setConstructorItems((prevItems) =>
      prevItems.filter((item) => item !== product)
    );
  };

  const handleAddToConstructor = (product) => {
    addToConstructor(product);
  };

  const handleDeleteFromConstructor = (product) => {
    deleteFromConstructor(product);
  };

  return (
    <div>
      <BurgerIngredients onAddToConstructor={handleAddToConstructor} />
      <BurgerConstructor
        constructorItems={constructorItems}
        onDeleteFromConstructor={handleDeleteFromConstructor}
      />
    </div>
  );
};

export default App;
