import React, { useState, useRef } from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { data } from "../../utils/data";
import BIStyle from "./BurgerIngredients.module.css";
import Card from "../Card/Card";

const Tabs = ({ current, setCurrent }) => {
  const bunRef = useRef<HTMLHeadingElement>(null);
  const sauceRef = useRef<HTMLHeadingElement>(null);
  const mainRef = useRef<HTMLHeadingElement>(null);

  const handleTabClick = (value) => {
    setCurrent(value);

    // Scroll to the selected tab
    if (value === "bun") {
      bunRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    } else if (value === "sauce") {
      sauceRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    } else if (value === "main") {
      mainRef.current.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  };

  return (
    <div style={{ display: "flex" }}>
      <Tab
        value="bun"
        active={current === "bun"}
        onClick={() => handleTabClick("bun")}
      >
        Булки
      </Tab>
      <Tab
        value="sauce"
        active={current === "sauce"}
        onClick={() => handleTabClick("sauce")}
      >
        Соусы
      </Tab>
      <Tab
        value="main"
        active={current === "main"}
        onClick={() => handleTabClick("main")}
      >
        Начинки
      </Tab>
    </div>
  );
};

const BurgerIngredients = ({ onAddToConstructor }) => {
  const [currentTab, setCurrentTab] = useState("bun");

  const handleTabChange = (value) => {
    setCurrentTab(value);
  };

  const handleAddToConstructor = (product) => {
    const updatedData = data.map((item) => {
      if (item._id === product._id) {
        return { ...item, count: (item.count || 0) + 1 };
      }
      return item;
    });
    onAddToConstructor(product);
  };

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_color_primary text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <Tabs current={currentTab} setCurrent={handleTabChange} />
      <div className={BIStyle["menu-container"]}>
        <h2
          ref={bunRef}
          className="text text_color_primary text_type_main-medium mt-10 mb-6"
        >
          Булки
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "bun") {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                ></Card>
              );
            }
            return null;
          })}
        </ul>
        <h2
          ref={sauceRef}
          className="text text_color_primary text_type_main-medium mt-10 mb-6"
        >
          Соусы
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "sauce") {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                ></Card>
              );
            }
            return null;
          })}
        </ul>
        <h2
          ref={mainRef}
          className="text text_color_primary text_type_main-medium mt-10 mb-6"
        >
          Начинка
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "main") {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                ></Card>
              );
            }
            return null;
          })}
        </ul>
      </div>
    </section>
  );
};

export default BurgerIngredients;
