import React from 'react';
import { CurrencyIcon, Counter } from '@ya.praktikum/react-developer-burger-ui-components';
import CStyle from './Card.module.css';

const Card = (props) => {
  const handleAddToConstructor = () => {
    props.addItem(props);
  };

  return (
    <li className={CStyle.card}>
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          position: 'relative',
          justifyContent: 'center',
        }}
        className="pl-4 pr-4"
      >
        <img src={props.image} alt={props.name} onClick={handleAddToConstructor} />
        {props.count && <Counter count={props.count} size="default" />}
      </div>
      {/* Rest of the code */}
    </li>
  );
};

export default Card;
