import React, { useState } from 'react';
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components';
import { data } from '../../utils/data';
import BIStyle from './BurgerIngredients.module.css';
import Card from '../Card/Card';

const Tabs = ({ current, setCurrent }) => {
  const handleTabClick = (value) => {
    setCurrent(value);
  };

  return (
    <div style={{ display: 'flex' }}>
      <Tab value="one" active={current === 'one'} onClick={handleTabClick}>
        Булки
      </Tab>
      <Tab value="two" active={current === 'two'} onClick={handleTabClick}>
        Соусы
      </Tab>
      <Tab value="three" active={current === 'three'} onClick={handleTabClick}>
        Начинки
      </Tab>
    </div>
  );
};

const MenuCategory = ({ category, onCardClick }) => {
  const categoryData = data.filter((item) => item.type === category);

  return (
    <React.Fragment>
      <h2 className="text text_color_primary text_type_main-medium mt-10 mb-6">
        {category === 'bun' ? 'Булки' : category === 'sauce' ? 'Соусы' : 'Начинки'}
      </h2>
      <ul className={BIStyle['card-container'] + ' pl-4 pr-4'}>
        {categoryData.map((item) => (
          <Card
            key={item._id}
            item={item}
            image={item.image}
            price={item.price}
            name={item.name}
            onCardClick={onCardClick}
          />
        ))}
      </ul>
    </React.Fragment>
  );
};

const BurgerIngredients = () => {
  const [currentTab, setCurrentTab] = useState('one');

  const handleCardClick = (item) => {
    // Implement the logic for adding the item to the burger constructor
    console.log('Selected item:', item);
  };

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_color_primary text_type_main-large mt-10 mb-5">Соберите бургер</h1>
      <Tabs current={currentTab} setCurrent={setCurrentTab} />
      <div className={BIStyle['menu-container']}>
        <MenuCategory category="bun" onCardClick={handleCardClick} />
        <MenuCategory category="sauce" onCardClick={handleCardClick} />
        <MenuCategory category="main" onCardClick={handleCardClick} />
      </div>
    </section>
  );
};

export default BurgerIngredients;
