}



  return (
    <section className={BIStyle.section}>
      <h1 className="text text_color_primary text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={currentTab === "bun"}
          onClick={() => onTabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={currentTab === "sauce"}
          onClick={() => onTabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={currentTab === "main"}
          onClick={() => onTabClick("main")}
        >
          Начинки
        </Tab>
      </div>

      <h2
        className="text text_color_inactive text_type_main-medium mt-10 mb-6"
        ref={bunRef}
      >
        Булки
      </h2>
      <ul className={BIStyle.cardsList}>
        {data.bun.map((item) => (
          <Card
            key={item._id}
            image={item.image}
            price={item.price}
            count={item.count}
            name={item.name}
            selectedItems={selectedItems}
            onClick={() => handleAddToConstructor(item)}
          />
        ))}
      </ul>

      <h2
        className="text text_color_inactive text_type_main-medium mt-10 mb-6"
        ref={sauceRef}
      >
        Соусы
      </h2>
      <ul className={BIStyle.cardsList}>
        {data.sauce.map((item) => (
          <Card
            key={item._id}
            image={item.image}
            price={item.price}
            count={item.count}
            name={item.name}
            selectedItems={selectedItems}
            onClick={() => handleAddToConstructor(item)}
          />
        ))}
      </ul>

      <h2
        className="text text_color_inactive text_type_main-medium mt-10 mb-6"
        ref={mainRef}
      >
        Начинки
      </h2>
      <ul className={BIStyle.cardsList}>
        {data.main.map((item) => (
          <Card
            key={item._id}
            image={item.image}
            price={item.price}
            count={item.count}
            name={item.name}
            selectedItems={selectedItems}
            onClick={() => handleAddToConstructor(item)}
          />
        ))}
      </ul>
    </section>
  );
};

export default BurgerIngredients;
