import React, { useState } from 'react';
import { ConstructorElement, Button } from '@ya.praktikum/react-developer-burger-ui-components';
import { data } from '../../utils/data';
import currency from '../../images/currency-icon.svg';
import BCStyle from './BurgerConstructor.module.css';

const BurgerConstructor = () => {
  const [selectedItems, setSelectedItems] = useState([]);

  const handleCardItemClick = (id) => {
    const selectedItem = data.find(item => item._id === id);
    if (selectedItem) {
      setSelectedItems(prevItems => [...prevItems, selectedItem]);
    }
  };

  return (
    <section className={BCStyle.section + ' mt-25'}>
      <div className={BCStyle.list}>
        {data.map(item => (
          <div
            key={item._id}
            className={BCStyle.card}
            onClick={() => handleCardItemClick(item._id)}
          >
            <img className={BCStyle.cardImage} src={item.image} alt={item.name} />
            <p className={BCStyle.cardText}>{item.name}</p>
          </div>
        ))}
      </div>
      <div className={BCStyle.selectedItems}>
        {selectedItems.map(item => (
          <ConstructorElement
            key={item._id}
            type="bottom"
            text={item.name}
            price={item.price}
            thumbnail={item.image}
          />
        ))}
      </div>
      <div
        style={{
          display: 'flex',
          justifyContent: 'flex-end',
          alignItems: 'center',
        }}
        className="mt-10"
      >
        <p className="text text_type_digits-medium mr-2">610</p>
        <img alt="валюта" src={currency} />
        <Button
          htmlType="submit"
          type="primary"
          size="large"
          style={{ marginLeft: '40px' }}
        >
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
