import React from "react";
import { data } from "../../utils/data";
import {
  ConstructorElement,
  Button,
  DragIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import currency from "../../images/currency-icon.svg";
import BCStyle from "./BurgerConstructor.module.css";

const BurgerConstructor = ({
  constructorItems,
  onDeleteFromConstructor,
  totalPrice,
  selectedItems = [7, 2, 8, 11, 11, 1, 3, 4, 10, 12, 13],
}) => {
  const handleDeleteFromConstructor = (productId, price, count) => {
    onDeleteFromConstructor(productId, price, count);
  };

  return (
    <section className={`${BCStyle.section} mt-25`}>
      <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        <div className={`${BCStyle.item} mb-4 ml-8`}>
          <ConstructorElement
            type="top"
            isLocked={true}
            text="Конструктор (верх)"
            price={0}
            thumbnail=""
          />
        </div>
        <ul className={BCStyle.list}>
          {selectedItems.map((itemId) => (
            <li className={BCStyle.item} key={itemId}>
              <DragIcon />
              <ConstructorElement
                text=""
                price={0}
                thumbnail=""
                handleClose={() =>
                  handleDeleteFromConstructor(
                    itemId,
                    data[itemId].price,
                    data[itemId].count
                  )
                }
              />
            </li>
          ))}
          {constructorItems.map((item) => (
            <li className={BCStyle.item} key={item.id}>
              <DragIcon />
              <ConstructorElement
                type={item.type}
                isLocked={item.isLocked}
                text={item.name}
                price={item.price}
                thumbnail={item.image}
                handleClose={() =>
                  handleDeleteFromConstructor(item.id, item.price, item.count)
                }
              />
            </li>
          ))}
        </ul>

        <div className={`${BCStyle.item} mt-4 ml-8`}>
          <ConstructorElement
            type="bottom"
            isLocked={true}
            text="Конструктор (низ)"
            price={0}
            thumbnail=""
          />
        </div>
      </div>
      <div className={`${BCStyle.footer} mt-10`}>
        <p className={`${BCStyle.price} text text_type_digits-medium mr-2`}>
          {610 + totalPrice}
        </p>
        <img alt="валюта" src={currency} />
        <Button type="primary" size="large" className={BCStyle.orderButton}>
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
