import React, { useState } from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { data } from "../../utils/data";
import BIStyle from "./BurgerIngredients.module.css";
import Card from "../Card/Card";

const Tabs = ({ setCurrent }) => {
  return (
    <div style={{ display: "flex" }}>
      <Tab value="one" active={current === "one"} onClick={setCurrent}>
        Булки
      </Tab>
      <Tab value="two" active={current === "two"} onClick={setCurrent}>
        Соусы
      </Tab>
      <Tab value="three" active={current === "three"} onClick={setCurrent}>
        Начинки
      </Tab>
    </div>
  );
};

const BurgerIngredients = ({ onCardItemClick }) => {
  const [current, setCurrent] = useState("one");

  const handleCardItemClick = (id) => {
    const selectedItem = data.find((item) => item._id === id);
    if (selectedItem) {
      onCardItemClick(selectedItem); // Pass the selected item to the callback function
    }
  };

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_color_primary text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <Tabs setCurrent={setCurrent} />
      <div className={BIStyle["menu-container"]}>
        <h2 className="text text_color_primary text_type_main-medium mt-10 mb-6">
          Булки
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "bun") {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={2}
                  name={item.name}
                  onClick={() => handleCardItemClick(item._id)} // Handle the click event
                />
              );
            }
            return null;
          })}
        </ul>
        {/* Rest of the code */}
      </div>
    </section>
  );
};

export default BurgerIngredients;
