// BurgerConstructor.js
import React from "react";
import { ConstructorElement, Button } from "@ya.praktikum/react-developer-burger-ui-components";
import currency from "../../images/currency-icon.svg";
import BCStyle from "./BurgerConstructor.module.css";

const BurgerConstructor = ({ constructorItems, onDeleteFromConstructor }) => {
  const handleDeleteFromConstructor = (productId) => {
    onDeleteFromConstructor(productId);
  };

  return (
    <section className={BCStyle.section + " mt-25"}>
      <div className={BCStyle.list}>
        {constructorItems.map((item) => (
          <ConstructorElement
            key={item.id}
            type={item.type}
            isLocked={item.isLocked}
            text={item.name}
            price={item.price}
            thumbnail={item.image}
            handleClose={() => handleDeleteFromConstructor(item.id)}
          />
        ))}
      </div>
      {/* Remaining code */}
    </section>
  );
};

export default BurgerConstructor;
