import React, { useState } from 'react';
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components/dist/ui/tab';
import { data } from '../../utils/data';
import BIStyle from './BurgerIngredients.module.css';
import Card from '../Card/Card';

const BurgerIngredients = ({ onAddToConstructor }) => {
  const [currentTab, setCurrentTab] = useState('bun');

  const onTabClick = (tab) => {
    setCurrentTab(tab);
    const refMap = {
      bun: bunRef,
      sauce: sauceRef,
      main: mainRef,
    };

    const ref = refMap[tab];
    if (ref && ref.current) {
      ref.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleAddToConstructor = (item) => {
    onAddToConstructor({ ...item, count: item.count + 1 });
  };

  const bunRef = React.createRef();
  const sauceRef = React.createRef();
  const mainRef = React.createRef();

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <div style={{ display: 'flex' }}>
        <Tab value="bun" active={currentTab === 'bun'} onClick={() => onTabClick('bun')}>
          Булки
        </Tab>
        <Tab value="sauce" active={currentTab === 'sauce'} onClick={() => onTabClick('sauce')}>
          Соусы
        </Tab>
        <Tab value="main" active={currentTab === 'main'} onClick={() => onTabClick('main')}>
          Начинки
        </Tab>
      </div>
      <div className={BIStyle.container + ' pl-4 pr-4'}>
        <h2 className="text text_type_main-medium mt-10 mb-6">
          Булки
        </h2>
        <ul className={`${BIStyle['card-list']} pl-4 pr-4`} ref={bunRef}>
          {data.map((item) => {
            if (item.type === 'bun') {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
        <h2 className="text text_type_main-medium mt-10">
          Соусы
        </h2>
        <ul className={`${BIStyle['card-list']} pl-4 pr-4`} ref={sauceRef}>
          {data.map((item) => {
            if (item.type === 'sauce') {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
        <h2 className="text text_type_main-medium mt-10">
          Начинка
        </h2>
        <ul className={`${BIStyle['card-list']} pl-4 pr-4`} ref={mainRef}>
          {data.map((item) => {
            if (item.type === 'main') {
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={item.count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
      </div>
    </section>
  );
};

export default BurgerIngredients;
