// App.js
import React, { useState } from 'react';
import AppHeader from '../AppHeader/AppHeader';
import BurgerConstructor from '../BurgerConstructor/BurgerConstructor';
import BurgerIngredients from '../BurgerIngredients/BurgerIngredients';
import AppStyle from './App.module.css';

const App = () => {
  const [constructorItems, setConstructorItems] = useState([]);

  const handleAddToConstructor = (item) => {
    setConstructorItems((prevItems) => [...prevItems, item]);
  };

  return (
    <div>
      <BurgerIngredients
        onAddToConstructor={handleAddToConstructor}
        constructorItems={constructorItems} // Pass constructorItems as a prop
      />
    </div>
  );
};

export default App;
