import React from "react";
import { data } from "../../utils/data";
import {
  ConstructorElement,
  Button,
  DragIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";
import currencyIcon from "../../images/currency-icon.svg";
import BCStyle from "./BurgerConstructor.module.css";

const BurgerConstructor = ({
  constructorItems,
  onDeleteFromConstructor,
  totalPrice,
  selectedItems = [],
}) => {
  const handleDeleteFromConstructor = (itemId) => {
    onDeleteFromConstructor(itemId);
  };

  const handleRemoveItemClick = (itemId, price, count) => {
    if (count > 0) {
      const updatedCount = count - 1;
      handleDeleteFromConstructor(itemId);
    }
  };

  const renderSelectedItems = () => {
    return selectedItems.map((itemId, index) => {
      const item = data.find((item) => item.id === itemId);
      if (item) {
        return (
          <li className={`${BCStyle.item} mr-4`} key={`selected-${itemId}-${index}`}>
            <DragIcon type="primary" />
            <ConstructorElement
              text={item.name}
              price={item.price}
              thumbnail={item.image}
              handleClose={() =>
                handleDeleteFromConstructor(itemId)
              }
            />
          </li>
        );
      }
      return null;
    });
  };

  const renderConstructorItems = () => {
    return constructorItems.map((item) => {
      return (
        <li className={`${BCStyle.item} mr-4`} key={`constructor-${item.id}`}>
          <DragIcon type="primary" />
          <ConstructorElement
            type={item.type}
            isLocked={item.isLocked}
            text={item.name}
            price={item.price}
            thumbnail={item.image}
            handleClose={() =>
              handleRemoveItemClick(item.id, item.price, item.count)
            }
          />
        </li>
      );
    });
  };

  return (
    <section className={`${BCStyle.section} mt-25`}>
      <div className={`${BCStyle["section-container"]} `}>
        <div className={`${BCStyle.item} mb-4 ml-8`}>
          <ConstructorElement
            type="top"
            isLocked={true}
            text={data[0].name + " (верх)"}
            price={data[0].price}
            thumbnail={data[0].image}
          />
        </div>
        <ul className={BCStyle.list}>
          {renderSelectedItems()}
          {renderConstructorItems()}
        </ul>
        <div className={`${BCStyle.item} mt-4 ml-8`}>
          <ConstructorElement
            type="bottom"
            isLocked={true}
            text={data[0].name + " (низ)"}
            price={data[0].price}
            thumbnail={data[0].image}
          />
        </div>
      </div>
      <div className={`${BCStyle["section-footer"]} mt-10 mr-6`}>
        <p className="text text_type_digits-medium" style={{ display: "flex" }}>
          {610 + totalPrice}
          <img className="ml-2" alt="валюта" src={currencyIcon} />
        </p>
        <Button htmlType="submit" type="primary" size="large">
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerConstructor;
