// BurgerIngredients.js
import React, { useState } from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { data } from "../../utils/data";
import BIStyle from "./BurgerIngredients.module.css";
import Card from "../Card/Card";

const Tabs = ({ current, setCurrent }) => {
  const handleTabClick = (value) => {
    setCurrent(value);
  };

  return (
    <div style={{ display: "flex" }}>
      <Tab value="one" active={current === "one"} onClick={handleTabClick}>
        Булки
      </Tab>
      <Tab value="two" active={current === "two"} onClick={handleTabClick}>
        Соусы
      </Tab>
      <Tab value="three" active={current === "three"} onClick={handleTabClick}>
        Начинки
      </Tab>
    </div>
  );
};

const BurgerIngredients = ({ onAddToConstructor }) => {
  const [currentTab, setCurrentTab] = useState("one");

  const handleTabChange = (value) => {
    setCurrentTab(value);
  };

  const handleAddToConstructor = (product) => {
    onAddToConstructor(product);
  };

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_color_primary text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <Tabs current={currentTab} setCurrent={handleTabChange} />
      <div className={BIStyle["menu-container"]}>
        <h2 className="text text_color_primary text_type_main-medium mt-10 mb-6">
          Булки
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "bun") {
              const count = constructorItems.filter(
                (constructItem) => constructItem.id === item.id
              ).length;
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
        <h2 className="text text_color_primary text_type_main-medium mt-10 mb-6">
          Соусы
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "sauce") {
              const count = constructorItems.filter(
                (constructItem) => constructItem.id === item.id
              ).length;
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
        <h2 className="text text_color_primary text_type_main-medium mt-10 mb-6">
          Начинка
        </h2>
        <ul className={BIStyle["card-container"] + " pl-4 pr-4"}>
          {data.map((item) => {
            if (item.type === "main") {
              const count = constructorItems.filter(
                (constructItem) => constructItem.id === item.id
              ).length;
              return (
                <Card
                  key={item._id}
                  image={item.image}
                  price={item.price}
                  count={count}
                  name={item.name}
                  onClick={() => handleAddToConstructor(item)}
                />
              );
            }
            return null;
          })}
        </ul>
      </div>
    </section>
  );
};

export default BurgerIngredients;
