import React, { useState, useEffect, useRef } from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";
import { data } from "../../utils/data";
import BIStyle from "./BurgerIngredients.module.css";
import Card from "../Card/Card";

const BurgerIngredients = ({ onAddToConstructor }) => {
  const [currentTab, setCurrentTab] = useState("bun");
  const bunRef = useRef(null);
  const sauceRef = useRef(null);
  const mainRef = useRef(null);
  const [selectedItems, setSelectedItems] = useState([]);

  const onTabClick = (e) => {
    setCurrentTab(e);
  };

  const handleAddToConstructor = (item) => {
    onAddToConstructor(item);
  };

  if (!data || Object.keys(data).length === 0) {
    return null; // or display a loading indicator or an empty state
  }

  useEffect(() => {
    if (selectedItems.includes(currentTab)) {
      return;
    }
    setSelectedItems([]);
  }, [currentTab]);

  const items = data[currentTab];

  return (
    <section className={BIStyle.section}>
      <h1 className="text text_color_primary text_type_main-large mt-10 mb-5">
        Соберите бургер
      </h1>
      <div style={{ display: "flex" }}>
        <Tab
          value="bun"
          active={currentTab === "bun"}
          onClick={() => onTabClick("bun")}
        >
          Булки
        </Tab>
        <Tab
          value="sauce"
          active={currentTab === "sauce"}
          onClick={() => onTabClick("sauce")}
        >
          Соусы
        </Tab>
        <Tab
          value="main"
          active={currentTab === "main"}
          onClick={() => onTabClick("main")}
        >
          Начинки
        </Tab>
      </div>

      <h2
        className="text text_color_inactive text_type_main-medium mt-10 mb-6"
        ref={bunRef}
      >
        Булки
      </h2>
      <ul className={BIStyle.cardsList}>
        {items.map((item) => (
          <Card
            key={item._id}
            image={item.image}
            price={item.price}
            count={item.count}
            name={item.name}
            selectedItems={selectedItems}
            onClick={() => handleAddToConstructor(item)}
          />
        ))}
      </ul>
    </section>
  );
};

export default BurgerIngredients;
