import React, { useState } from 'react';
import { ConstructorElement, Button } from '@ya.praktikum/react-developer-burger-ui-components';
import { data } from '../../utils/data';
import currency from '../../images/currency-icon.svg';
import BCStyle from './BurgerConstructor.module.css';

const BurgerConstructor = () => {
  const [selectedItems, setSelectedItems] = useState([]);

  const handleItemClick = (item) => {
    // Check if the item is already selected
    const itemIndex = selectedItems.findIndex((selectedItem) => selectedItem.id === item.id);

    if (itemIndex !== -1) {
      // If selected, remove the item from the selectedItems array
      setSelectedItems((prevItems) => prevItems.filter((prevItem) => prevItem.id !== item.id));
    } else {
      // If not selected, add the item to the selectedItems array
      setSelectedItems((prevItems) => [...prevItems, item]);
    }
  };

  const calculateTotalPrice = () => {
    // Calculate the total price based on the selected items' prices
    const totalPrice = selectedItems.reduce((acc, item) => acc + item.price, 0);
    return totalPrice;
  };

  return (
    <section className={BCStyle.section + " mt-25"}>
      <div className="mb-4 ml-8">
        <ConstructorElement
          type="top"
          isLocked={true}
          text={data[0].name + " (верх)"}
          price={20}
          thumbnail={data[0].image}
          onClick={() => handleItemClick(data[0])}
          isSelected={selectedItems.some((item) => item.id === data[0].id)}
        />
      </div>
      <div className={BCStyle.list}></div>
      <div className="mt-4 ml-8">
        <ConstructorElement
          type="bottom"
          isLocked={true}
          text={data[0].name + " (вниз)"}
          price={20}
          thumbnail={data[0].image}
          onClick={() => handleItemClick(data[0])}
          isSelected={selectedItems.some((item) => item.id === data[0].id)}
        />
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          alignItems: "center",
        }}
        className="mt-10"
      >
        <p className="text text_type_digits-medium mr-2">{calculateTotalPrice()}</p>
        <img alt="валюта" src={currency} />
        <Button
          htmlType="submit"
          type="primary"
          size="large"
          style={{ marginLeft: "40px" }}
        >
          Оформить заказ
        </Button>
      </div>
    </section>
  );
}

export default BurgerConstructor;
