import React, { useState } from 'react';
import { ConstructorElement, Button } from '@ya.praktikum/react-developer-burger-ui-components';
import { data } from '../../utils/data';
import currency from '../../images/currency-icon.svg';
import BCStyle from './BurgerConstructor.module.css';
import BurgerIngredients from '../BurgerIngredients/';

const BurgerBuilder = () => {
  const [selectedItems, setSelectedItems] = useState([]);

  const handleCardItemClick = (id) => {
    const selectedItem = data.find(item => item._id === id);
    if (selectedItem) {
      setSelectedItems(prevItems => [...prevItems, selectedItem]);
    }
  };

  return (
    <div>
      <BurgerIngredients onCardItemClick={handleCardItemClick} />
      <BurgerConstructor selectedItems={selectedItems} />
    </div>
  );
};

const BurgerConstructor = ({ selectedItems }) => {
  return (
    <section className={BCStyle.section + ' mt-25'}>
      <div className={BCStyle.selectedItems}>
        {selectedItems.map(item => (
          <ConstructorElement
            key={item._id}
            type="bottom"
            text={item.name}
            price={item.price}
            thumbnail={item.image}
          />
        ))}
      </div>
      <div
        style={{
          display: 'flex',
          justifyContent: 'flex-end',
          alignItems: 'center',
        }}
        className="mt-10"
      >
        <p className="text text_type_digits-medium mr-2">610</p>
        <img alt="валюта" src={currency} />
        <Button
          htmlType="submit"
          type="primary"
          size="large"
          style={{ marginLeft: '40px' }}
        >
          Оформить заказ
        </Button>
      </div>
    </section>
  );
};

export default BurgerBuilder;
